package solution.leetcode;

public class FindTheDifference {
    public static void main(String[] args) {
        FindTheDifference ftd = new FindTheDifference();
        System.out.println(ftd.findTheDifference("abcd", "abcde")); // e
        System.out.println(ftd.findTheDifference("a", "aa")); // a
        System.out.println(ftd.findTheDifference("", "y")); // y
        System.out.println(ftd.findTheDifference("ae", "aea")); // a
        System.out.println(ftd.findTheDifference("", ""));
    }
    public char findTheDifference(String s, String t) {
        int s1 = 0;
        int t1 = t.charAt(t.length()-1);
        for(int i = 0; i<s.length(); i++){
            s1 += s.charAt(i);
            t1 += t.charAt(i);
        }
        int result = t1-s1;
        return (char)result;
    }
}
